$(document).ready(function(){$("#datetimepicker1").datetimepicker({format:"MM/DD/YYYY",daysOfWeekDisabled:[0,6]});$("#datetimepicker2").datetimepicker({useCurrent:!1,format:"MM/DD/YYYY",daysOfWeekDisabled:[0,6]});$("#datetimepicker1").on("dp.change",function(n){$("#datetimepicker2").data("DateTimePicker").minDate(n.date);$("#datetimepicker3").data("DateTimePicker").clear();$("#datetimepicker4").data("DateTimePicker").clear()});$("#datetimepicker2").on("dp.change",function(n){$("#datetimepicker1").data("DateTimePicker").maxDate(n.date);$("#datetimepicker3").data("DateTimePicker").clear();$("#datetimepicker4").data("DateTimePicker").clear()});$("#datetimepicker3").datetimepicker({format:"HH:mm",useCurrent:!1,minDate:moment({h:00,m:01}),maxDate:moment({h:23,m:45}),stepping:15});$("#datetimepicker4").datetimepicker({format:"HH:mm",useCurrent:!1,minDate:moment({h:00,m:01}),maxDate:moment({h:23,m:45}),stepping:15});$("#datetimepicker3").on("dp.change",function(n){$("#startDate").val()==$("#endDate").val()?$("#datetimepicker4").data("DateTimePicker").minDate(n.date):$("#datetimepicker4").data("DateTimePicker").minDate(!1)});$("#datetimepicker4").on("dp.change",function(n){$("#startDate").val()==$("#endDate").val()?$("#datetimepicker3").data("DateTimePicker").maxDate(n.date):$("#datetimepicker3").data("DateTimePicker").maxDate(!1)})});