var handleDateTimeField=function(n,t){let i=$("#"+n),r=$("label[for='"+n+"']");i.val()&&(document.getElementById(n).type=t);i.on("focus",()=>{document.getElementById(n).type=t,r.hide()});i.on("focusout",()=>{i.val()||(document.getElementById(n).type="text",r.show())})},initializeDateFields=function(n,t){let i=$("#"+n),r=$("#"+t),u=i.val();u&&r.attr("min",u);let f=r.val();f&&i.attr("max",f)},syncDateFields=function(n,t){let i=$("#"+n),r=$("#"+t);i.on("change",()=>{let n=i.val();n?r.attr("min",n):r.attr("min","");n>r.val()&&r.val(n)});r.on("change",()=>{let n=r.val();n?i.attr("max",n):i.attr("max","");n<i.val()&&i.val(n)})},syncTimeFields=function(n,t){let i=$("#"+n),r=$("#"+t);i.on("change",()=>{let n=i.val();n>r.val()&&r.val(n)});r.on("change",()=>{let n=r.val();n<i.val()&&i.val(n)})};$(document).ready(function(){let n="startDate",i="startTime",t="endDate",r="endTime";handleDateTimeField(n,"date");handleDateTimeField(t,"date");initializeDateFields(n,t);syncDateFields(n,t);handleDateTimeField(i,"time");handleDateTimeField(r,"time");let u=$("#"+n).val()==$("#"+t).val();u&&syncTimeFields(i,r)});